sp_widget.client_script:
api.controller=function($rootScope, $scope) {
	/* widget controller */
	var c = this;

	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('%cNote ID: ' + id,'color:green');
		$rootScope.noteID = id;

		// Emit/broadcast an event so that listening widgets can react/respond accordingly. 
		$rootScope.$emit('selectNote', id);
	}

	$rootScope.$on('refreshNotes', function(event, data) {
		// Tells the server to refresh/reload the data object. 
		c.server.refresh();
	});

};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	data.notes = [];

	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();

	while(noteGR.next()) {
		var noteObj = {};

		// Copies the display values of the 3 fields from the GlideRecord object into the noteGr object
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id'); 

		//get the first 20 characters of the description
		noteObj.note = noteGR.getValue('note').slice(0,20);

		data.notes.push(noteObj);
	}
})();
